import React, { useState, useEffect } from 'react';

const Pendaftaran = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResult, setSearchResult] = useState([]);

  const [datapasien, setDataPasien] = useState([]);
  const [iddatapasienid, setIdDataPasien] = useState();

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleklik = (event, id) => {
    event.preventDefault();
    setSearchResult([]);
    setSearchQuery('');
    setIdDataPasien(id);

    fetch(`http://localhost:5000/igd/pasien/${id}`)
      .then((response) => response.json())
      .then((datak) => {
        setDataPasien(datak.data);
        console.log(datak.data);
      })
      .catch((error) => console.log(error));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    fetch(`http://localhost:5000/igd/pasien?search=${searchQuery}&page=&limit=10`)
      .then((response) => response.json())
      .then((data) => setSearchResult(data.data))
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    if (iddatapasienid) {
      fetch(`http://localhost:5000/igd/pasien/${iddatapasienid}`)
        .then((response) => response.json())
        .then((datak) => {
          setDataPasien(datak.data);
          console.log(datak.data);
        })
        .catch((error) => console.log(error));
    }
  }, [iddatapasienid]);

  return (
    <>
      <div className="container border border-state-300 bg-white p-5">
        <div className="search text-left flex">
          <form onSubmit={handleSubmit}>
            <input
              className="border border-black pl-0.5 py-0.4"
              type="text"
              value={searchQuery}
              onChange={handleInputChange}
              placeholder="Search..."
              autoFocus
            />
            <button className="ml-1 py-0.2 px-1 bg-emerald text-white rounded hover:opacity-75">
              Submit
            </button>
          </form>
        </div>

        <ul className="text-left mt-5">
          {searchResult.map((item) => (
            <li key={item.id}>
              <a
                href=""
                className="hover:bg-slate-700"
                onClick={(event) => handleklik(event, item.id)}
              >
                <p>nama lengkap : {item.nama_lengkap}</p>
              </a>
            </li>
          ))}
        </ul>
      </div>

      <div className="container">
        {datapasien.map((pasien) => (
          <li key={pasien.id}>{pasien.nama_lengkap}</li>
        ))}
      </div>
    </>
  );
};

export default Pendaftaran;
