import React from 'react';
import { useEffect, useState, useCallback } from 'react';
import axios from "axios";
import useSWR from 'swr';
// import useSWRPages from 'swr/dist/types/use-swr-pages';
import useSWRPages from 'swr/esm/use-swr-pages';


const LiveSearchWithPagination = () => {

const [searchTerm, setSearchTerm] = useState('');
const [currentPage, setCurrentPage] = useState(1);

const handleSearchTermChange = useCallback((event) => {
  setSearchTerm(event.target.value);
  setCurrentPage(1);
}, []);

const fetcher = async (url) => {
  const response = await axios.get(url);
  return response.data;
};

const getData = (pageIndex, previousPageData) => {
  if (previousPageData && !previousPageData.length) return null;

  const offset = (pageIndex - 1) * 10;
  const url = `http://localhost:5000/rm?searchTerm=${searchTerm}&page=${pageIndex}&offset=${offset}`;

  return fetcher(url);
};

const {
  pages,
  isLoadingMore,
  isReachingEnd,
  loadMore,
} = useSWRPages(
  'data',
  getData,
  ({ data }) => data.length,
  [searchTerm, currentPage]
);


  return (
    <div>
    
    <input type="text" value={searchTerm} onChange={handleSearchTermChange} />

    {pages[currentPage - 1].map((item) => (
      <div key={item.id}>{item.name}</div>
    ))}

    {!isReachingEnd && (
      <button onClick={() => setCurrentPage((prev) => prev + 1)}>Load More</button>
    )}

    {isLoadingMore && <div>Loading...</div>}
    </div>
  )
}

export default LiveSearchWithPagination
