import React, {useState, useEffect} from 'react'
import axios from "axios";
import { Link, useNavigate , useParams} from "react-router-dom";


const Triase = () => {
    const {id} = useParams();
    const [pasienigdtriase, setPasienIgdTriase] = useState();
    const [isIdPasienIgd, setIsIdPasienIgd] = useState('');
    // const IdPasienIgd = pasienigdtriase[0].id_pasien_igd;
    const nilai = Number(id);


  
    
    function fetchTriaseIgd(id) {
      axios
      .get(`http://localhost:5000/igd/pasien/penanganan/triase/${id}`)
       .then(response => {
        const dataObject = response.data;
        const array = Object.values(dataObject);
         setPasienIgdTriase(array);
      })
      .catch((error) => {
        console.error(error);
      });
    }
    
  
 

  console.log(pasienigdtriase);
  
  


  useEffect(() => {
    fetchTriaseIgd(id);
  }, [id]);



  return (
<>
    <div className='container text-left'>
     
            { nilai === 17  ? (
                <div className='keluhan'>
                    {pasienigdtriase && pasienigdtriase.map((data, idx) => (
                        <div key={idx}>
                            Keluhan: {data.keluhan_utama}
                        </div>
                    ))}
                </div>
                
            ) : (
                <form>
                    Keluhan : <input type="text" className='border border-black' />
                    <button className='ml-1 py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75' type='button'>SIMPAN</button>
                </form>
            )}
    </div>


</>
  )
}

export default Triase
