import React, { useState, useEffect } from "react";
import axios from "axios";
import { API_URL } from "../../../../config";

const TambahPenjualan = () => {
  const [rowData, setRowData] = useState([
    {
      id_obat: "",
      jumlah: "",
      harga_satuan: "",
      diskon: "",
      total_harga: "",
      tanggal_penjualan: "",
      informasi_pembayaran: "",
      metode_pembayaran: "",
    },
  ]);

  const deleteRow = (index) => {
    let data = [...rowData];
    data.splice(index, 1);
    setRowData(data);
  };

  const [totalHarga, setTotalHarga] = useState();
  const jumlahTotalHarga = () => {
    let total = 0;

    rowData.forEach((row) => {
      // Assuming that row.harga_satuan and row.jumlah are numbers
      const hargaSatuan = Number(row.harga_satuan);
      const jumlah = Number(row.jumlah);

      const subTotal = hargaSatuan * jumlah;
      total += subTotal;
    });

    setTotalHarga(total);
  };

  const addRow = () => {
    let newRow = {
      id_obat: "",
      jumlah: "",
      harga_satuan: "",
      diskon: "",
      total_harga: "",
      tanggal_penjualan: "",
      informasi_pembayaran: "",
      metode_pembayaran: "",
    };
    setRowData([...rowData, newRow]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = rowData.map((row) => ({
        id_obat: Number(row.id_obat),
        jumlah: row.jumlah,
        harga_satuan: row.harga_satuan,
        diskon: row.diskon,
        total_harga: row.total_harga,
        tanggal_penjualan: "12-12-2023",
        informasi_pembayaran: "lunas",
        metode_pembayaran: "cash",
      }));

      const sendData = await axios.post(
        `${API_URL}/farmasi/obat/penjualan`,
        data
      );

      // console.log(sendData);
    } catch (error) {
      console.log(error);
    }
  };

  const handleFormPenjualanChange = (index, event) => {
    let data = [...rowData];
    data[index][event.target.name] = event.target.value;
    setRowData(data);
  };

  return (
    <div>
      <div className="container bg-emerald300 text-left pl-2 mt-3 py-0.5">
        <div className="flex justify-between px-4">
          <p>Input Penjualan</p>
          <button onClick={addRow}>Add Rows</button>
        </div>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="overflow-y-auto max-h-[48vh]">
          <table class="table-auto w-full">
            <thead className="sticky top-0 ">
              <tr>
                <th class=" py-3 px-6 bg-gray-50  text-xs font-medium text-gray-500 uppercase tracking-wider">
                  No
                </th>
                <th class=" py-3 px-6 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Nama Obat
                </th>
                <th class=" py-3 px-6 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Quantity
                </th>
                <th class=" py-3 px-6 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Harga
                </th>
                <th class=" py-3 px-6 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Diskon
                </th>
                <th class=" py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Total
                </th>
                <th class=" py-3 px-6 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"></th>
              </tr>
            </thead>
            <tbody class="bg-white divide-y divide-gray-200 ">
              {rowData.map((input, index) => (
                <tr
                  key={index}
                  className="border text-center border-gray-300 mx-4"
                >
                  <td className="py-0.3  px-6 whitespace-nowrap">
                    {index + 1}
                  </td>
                  <input
                    className="hover:border text-center hover:border-gray-300 text-sm w-full px-1"
                    name="id_obat"
                    type="text"
                    value={input.id_obat}
                    onChange={(event) =>
                      handleFormPenjualanChange(index, event)
                    }
                  />
                  <td className="py-0.3  px-6 whitespace-nowrap">
                    <input
                      class="hover:border text-center hover:border-gray-300  text-sm w-full px-1 "
                      name="jumlah"
                      type="text"
                      value={input.jumlah}
                      onChange={(event) =>
                        handleFormPenjualanChange(index, event)
                      }
                    />
                  </td>
                  <td className="py-0.3  px-6 whitespace-nowrap">
                    <input
                      class="hover:border text-center hover:border-gray-300  text-sm w-full px-1 "
                      name="harga_satuan"
                      type="text"
                      value={input.harga_satuan}
                      onChange={(event) =>
                        handleFormPenjualanChange(index, event)
                      }
                    />
                  </td>
                  <td className="py-0.3  px-6 whitespace-nowrap">
                    <input
                      class="hover:border text-center hover:border-gray-300  text-sm w-full px-1 "
                      name="diskon"
                      type="text"
                      value={input.diskon}
                      onChange={(event) =>
                        handleFormPenjualanChange(index, event)
                      }
                    />
                  </td>
                  <td className="py-0.3  px-6 whitespace-nowrap">
                    <input
                      class="hover:border text-center hover:border-gray-300  text-sm w-full px-1 "
                      name="total_harga"
                      type="text"
                      value={input.total_harga}
                      onChange={(event) =>
                        handleFormPenjualanChange(index, event)
                      }
                    />
                  </td>
                  <td className="py-0.3  px-6 whitespace-nowrap">
                    <button onClick={() => deleteRow(input.id)}>Delete</button>
                  </td>
                </tr>
              ))}
              <tr>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td className="text-right">Total : </td>
                <td onChange={jumlahTotalHarga}>{totalHarga}</td>
                <td></td>
              </tr>
            </tbody>
          </table>
          <div className="flex flex-row space-x-4 my-2 justify-end sticky">
            <p className="text-sm">
              Tanggal : <input type="tanggal_penjualan" />
            </p>
            <p className="text-sm"> Metode Pembayaran : Cash </p>
            <p className="text-sm pr-40">Status Pembayaran : Lunas</p>
          </div>
        </div>
        <div className="flex justify-end mr-4">
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default TambahPenjualan;
