                <input class="appearance-none bg-white border rounded w-full py-2 mb-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="alamat_pasien_kota" name='alamat_pasien_kota' type="text" onChange={handleFormChange} />
               
                <input class="appearance-none bg-white border rounded w-full py-2 mb-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="alamat_pasien_kec" name='alamat_pasien_kec' type="text" onChange={handleFormChange} />
               
                <input class="appearance-none bg-white border rounded w-full py-2 mb-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="alamat_pasien_desa" name='alamat_pasien_desa' type="text" onChange={handleFormChange} />
                
                <input class="appearance-none bg-white border rounded w-full py-2 mb-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="alamat_pasien_detail" name='alamat_pasien_detail' type="text" onChange={handleFormChange} />
                
            </div>
         <div className='border border-state-300 p-2'>
            <div className='field my-2'>
                <label class="block text-gray-700 text-sm text-left font-bold mb-2">Alamat penanggungjawab</label>
            <div className='container flex mb-2'>
                <div class="flex items-center mr-2">
                    <input
                        id="alamat_pj_detail1"
                        name="alamat_pj_detail"
                        value="Sama Seperti Pasien"
                        type="radio"
                        class="form-radio h-4 w-4 text-indigo-600"
                        onChange={handleFormChange}
                    />
                    <label for="kelamin" class="ml-1 text-gray-700">Sama seperti pasien</label>
                </div>
                <div class="flex items-center mr-2">
                    <input
                        id="alamat_pj_detail2"
                        name="alamat_pj_detail"
                        value="Beda dari Pasien"
                        type="radio"
                        class="form-radio h-4 w-4 text-indigo-600"
                        onChange={handleFormChange}
                    />
                    <label for="kelamin" class="ml-1 text-gray-700">Berbeda dari pasien</label>
                </div>
            </div>
                <input class="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="first-name" type="text" placeholder="Masukkan Nomor Indentitas" />
            </div>
        </div>
        </div>


        </div>
        </div>
        </div>
      <div class="bg-gray-100 p-4 flex justify-end">
        <button type="submit" class="px-4 py-2 bg-emerald hover:opacity-75 text-white rounded-md  focus:outline-none focus:ring-2 focus:ring-gray-600 focus:ring-opacity-50" onclick="toggleModal()">
          Submit
        </button>
      </div>
    </form>

    
    </div>
</Modal>
 {/* end modal edit */}


{/* modal delete */}
<Modal isOpen={isDeleteOpen} onClose={handleCloseModal}>
     <div class="bg-white rounded-lg w-1/3 mt-10 overflow-hidden shadow-xl transform transition-all max-w-screen-lg ">

      <div class="bg-gray-50 p-6">

  
        <div class="flex flex-col md:flex-row">
            <div class="w-full">
                   <h2 class="text-xl font-bold py-4 ">Are you sure?</h2>
                        <p class="text-sm text-gray-500 px-8">Do you really want to delete this data? This process cannot be undone</p> 
                  <div className='mt-4'>
                   <button onClick={handleConfirmDelete} class="px-4 py-2 mr-2 bg-red-800 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-600 focus:ring-opacity-50">Yes</button>
                   <button onClick={() => setIsDeleteOpen(false)} class="px-4 py-2 ml-2 bg-gray-100 text-state-700 border border-black rounded-md hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-600 focus:ring-opacity-50 mr-2">No</button>
                  </div>
            </div>
        </div>
        </div>
    </div>
</Modal>
 
{/* end modal delete */}

<div className='container px-5 my-5 py-5 border border-gray-300 bg-white '>
<div className='container mx-auto w-full mt-4 flex justify-between mb-1'>
{/* search  */}
    <form>
        <input className='border border-black pl-0.5 py-0.4  ' type="text" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} placeholder="Search..." autoFocus/>
    </form>
 {/* button add data */}
     <button onClick={() => setIsOpen(true)} className='bg-emerald hover:opacity-75 text-white font-bold py-0.3 px-2 rounded '>
        <div class="flex items-center">
        <svg version="1.0" xmlns="http://www.w3.org/2000/svg"
        width="13pt" height="13pt" viewBox="0 0 512.000000 512.000000"
        preserveAspectRatio="xMidYMid meet">
        <g transform="translate(0.000000,512.000000) scale(0.100000,-0.100000)"
        fill="#ffff" stroke="none">
        <path d="M2 4533 c0 -324 3 -572 5 -553 39 366 253 715 568 927 157 105 378
        186 565 206 19 2 -229 5 -552 5 l-588 2 2 -587z"/>
        <path d="M3970 5114 c188 -27 313 -65 452 -136 380 -194 646 -579 691 -998 2
        -19 5 229 5 553 l2 587 -587 -1 c-324 -1 -577 -3 -563 -5z"/>
        <path d="M1105 4675 c-333 -73 -590 -332 -661 -665 -20 -97 -20 -2803 0 -2900
        36 -170 113 -309 241 -435 96 -95 203 -161 335 -207 l95 -33 1405 -3 c972 -2
        1424 1 1468 8 159 27 346 126 459 242 112 116 196 272 229 428 11 53 14 309
        14 1446 0 949 -3 1399 -11 1439 -52 280 -261 535 -524 636 -158 61 -83 59
        -1600 58 -1113 0 -1402 -3 -1450 -14z m1533 -851 c44 -18 87 -57 110 -99 15
        -26 18 -86 22 -490 l5 -460 460 -5 c503 -5 487 -3 547 -67 76 -80 76 -206 0
        -286 -60 -64 -44 -62 -547 -67 l-460 -5 -5 -460 c-5 -503 -3 -487 -67 -547
        -80 -76 -206 -76 -286 0 -64 60 -62 44 -67 547 l-5 460 -460 5 c-503 5 -487 3
        -547 67 -76 80 -76 206 0 286 60 64 44 62 547 67 l460 5 5 460 c5 503 3 487
        67 547 59 56 148 73 221 42z"/>
        <path d="M2 588 l-2 -588 588 2 c323 0 571 3 552 5 -419 45 -806 313 -999 693
        -67 130 -119 304 -134 440 -2 19 -5 -229 -5 -552z"/>
        <path d="M5110 1133 c-1 -53 -33 -193 -65 -283 -112 -308 -336 -562 -625 -710
        -130 -66 -304 -119 -440 -133 -19 -2 229 -5 553 -5 l587 -2 0 585 c0 322 -2
        585 -5 585 -3 0 -5 -17 -5 -37z"/>
        </g>
        </svg>

         <span class="ml-0.5 mb-0.5">Pasien</span>
        </div>
     </button>

</div>            
            <div className="container mx-auto  py-3  bg-white">
                {/* prev and next page */}
            <div className='container mx-auto w-full flex justify-end pr-2'>
                <button onClick={() => setPage((page) => page - 1)} disabled={page === 0} type="button" class="bg-white text-state-400 rounded-l-md border border-black border-l  mr-1">
                <div class="flex flex-row align-middle">
                    <svg class="w-4 mr-1 mt-0.5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M7.707 14.707a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l2.293 2.293a1 1 0 010 1.414z" clip-rule="evenodd"></path>
                    </svg>
                    <p class="ml-0.5 mr-1 text-sm">Prev</p>
                </div>
                </button>

                <button onClick={() => {setPage((page) => page + 1);}} disabled={page === data.totalPages-1}  type="button" class="bg-white text-state-400 border border-black rounded-r-md border-l ">
                <div class="flex flex-row align-middle">
                    <p class="mr-0.5 ml-0.5 text-sm">Next</p>
                    <svg class="w-4 mr-1 mt-0.5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M12.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-2.293-2.293a1 1 0 010-1.414z" clip-rule="evenodd"></path>
                    </svg>
                </div>
                </button>
            </div>
                <table className="min-w-full divide-y divide-gray-200">
                    <thead>
                        <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">No RM</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Nama</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Kitas</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Kelamin</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal Lahir</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                    </thead>
                    
                    <tbody className="bg-white divide-y divide-gray-200">
                        {data.data.map((pasien, index) => (

                        <tr key={pasien.id}>

                        <td className="px-0 py-1 whitespace-nowrap">
                            <div className="text-sm text-gray-900"> {pasien.no_rm}</div>
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.nama_lengkap}
                        </td>
                        <td className="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.no_kitas}
                        </td>
                        <td className="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.kelamin}
                        </td>
                        <td className="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.tanggal_lahir}
                        </td>
            
                        <td className="px-0 py-0 whitespace-nowrap text-center text-sm font-medium">
                            <button   className="text-indigo-600 hover:text-indigo-900">  <img src={detailIcon} className="m-1" title='Detail' width="20px" alt="detail" /></button>
                            <button onClick={() => handleEdit(pasien)}  className="text-indigo-600 hover:text-indigo-900"><img src={editIcon} width="20px" className="m-1" title='Edit' alt="edit" /></button>
                             <button  onClick={() => handleShowModal(pasien.id)} className=" text-red-600 hover:text-red-900"><img src={deleteIcon} width="20px" className="m-1" title='Delete' alt="delete" /></button>
                        </td>
                        </tr>
                  
                    ))}
                    
                    </tbody>
                 
                </table>
              

            </div>
     <div className='container mx-auto w-full flex justify-start mt-1 ml-1'>
          <p className='text-sm'>Jumlah Data: {data.totalRows} dari {data.totalPages} halaman</p>
      </div>


</div>


        </div>
    </div>
  )
}

export default Pasien
