import React, { useState, useEffect } from "react";

const DaftarPasien = () => {
  // modal delete
  const [isDeleteOpen, setIsDeleteOpen] = useState(false);
  // penyimpanan sementara id delete
  const [deleteUserId, setDeleteUserId] = useState();
  // page register pasien
  const [isRegisterPasienRanap, setIsRegisterPasienRanap] = useState(false);
  // search bar
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [tampilanSearch, setTampilanSearch] = useState(false);
  // penyimpanan data sesuai yang dipilih pada saat search di kolom search register
  const [datapasien, setDataPasien] = useState([]);
  // post data register
  const [formData, setFormData] = useState({});
  const [simpanid, setSimpanId] = useState();

  // pagination
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(10);
  const [totalPages, setTotalPages] = useState();

  // retrive all pasien ranap
  const [allPasienRanap, setAllPasienRanap] = useState([]);
  // kolom pencarian pasien ranap
  const [searchQueryPasienRanap, setSearchQueryPasienRanap] = useState("");

  // retrive data kamar available
  const [kamarAvailable, setKamarAvailable] = useState([]);

  // no kamar yang dipilih saat pendaftaran ranap
  const [noKamar, setNoKamar] = useState("");
  // no bad yang dipilih saat pendaftaran ranap
  const [noBad, setNoBad] = useState("");
  // id kamar ranap untuk perubahan status kamar menjadi occupied
  const [idKamarRanap, setIdKamarRanap] = useState();

  // livesearch
  useEffect(() => {
    if (searchQuery !== "") {
      fetch(`${API_URL}/igd/pasien?search=${searchQuery}&page=&limit=5`)
        .then((response) => response.json())
        .then((data) => setSearchResult(data.data))
        .catch((error) => console.error(error));
    } else {
      setSearchResult([]);
    }
  }, [searchQuery]);

  //all pasien ranap
  useEffect(() => {
    const fetchAllDataPasienRanap = async () => {
      try {
        if (searchQueryPasienRanap !== "") {
          const response = await axios.get(
            `${API_URL}/ranap/pasien/all?search=${searchQueryPasienRanap}&page=1&limit=${limit}`
          );
          setAllPasienRanap(response.data.data);
          setTotalPages(response.data.totalPages);
        } else {
          const response = await axios.get(
            `${API_URL}/ranap/pasien/all?search=${searchQueryPasienRanap}&page=${page}&limit=${limit}`
          );
          setAllPasienRanap(response.data.data);
          setTotalPages(response.data.totalPages);
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchAllDataPasienRanap();
  }, [searchQueryPasienRanap, page, allPasienRanap, limit]);

  const handleDaftar = () => {
    setIsRegisterPasienRanap(true);
  };

  const handleCloseModal = () => {
    setIsDeleteOpen(false);
  };

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchPasienRanapChange = (event) => {
    setSearchQueryPasienRanap(event.target.value);
  };

  const handleCloseTampilan = () => {
    setIsRegisterPasienRanap(false);
  };

  const handleFormChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleShowModalDelete = (id) => {
    setIsDeleteOpen(true);
    setDeleteUserId(id);
  };
  const handleConfirmDelete = () => {
    handleDeleteUser(deleteUserId);
    setIsDeleteOpen(false);
    setDeleteUserId(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(
        `${API_URL}/igd/pasien?search=${searchQuery}&page=&limit=10`
      );
      setSearchResult(response.data.data);
    } catch (error) {
      console.log(error);
    }
  };

  // untuk mengambil data kamar available dari api dan di render di useEffect
  const fetchKamarAvailable = async () => {
    try {
      const response = await axios.get(
        `${API_URL}/ranap/pasien/register/kamar/available`
      );
      setKamarAvailable(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchKamarAvailable();
  }, [kamarAvailable]);

  // menangkap perubahan kamar pada form register dan id yang dikirimkan untuk update status kamar di db kamar_ranap
  const handleSelectKamarAvailable = (event) => {
    const data = event.target.value;
    const [noKamar, noBad, idKamar] = data.split(",");
    setNoKamar(noKamar);
    setNoBad(noBad);
    setIdKamarRanap(Number(idKamar));
  };

  const handleklik = (event, id) => {
    event.preventDefault();
    setSearchResult([]);
    setSearchQuery("");
    setTampilanSearch(true);
    fetch(`${API_URL}/igd/pasien/search/${id}`)
      .then((response) => response.json())
      .then((datak) => {
        setDataPasien([datak]);
        setSimpanId(parseInt(datak.id));
      })
      .catch((error) => console.log(error));
  };

  const handleRegisterPasien = async (event) => {
    event.preventDefault();
    const dataToSend = {
      ...formData,
      id_pasien_rm: simpanid,
      no_kamar: noKamar,
      no_bad: noBad,
    };
    const statusKamar = {
      status_kamar: "occupied",
    };
    try {
      const [response1, response2] = await Promise.all([
        axios.post(`${API_URL}/ranap/pasien/register`, dataToSend),
        axios.patch(
          `${API_URL}/ranap/kamar/status/${idKamarRanap}`,
          statusKamar
        ),
      ]);

      setTampilanSearch(false);
      setIsRegisterPasienRanap(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteUser = async (PasienId) => {
    try {
      await axios.delete(`${API_URL}/ranap/pasien/${PasienId}`);
    } catch (error) {
      console.error(error);
      alert("Failed to delete user.");
    }
  };

  return (
    <div class="container mx-auto 2xl:w-screen px-8 pb-10">
      <div className="container border border-state-300 bg-white p-3 mt-5  ">
        <div className="flex justify-end mb-4">
          <button
            className="py-0.2 text-sm  px-1 mr-1 bg-white border text-black border-black  hover:bg-emerald300"
            type="button"
          >
            DAFTAR PASIEN
          </button>
        </div>
        <div className="search text-left flex">
          <div className="search text-left flex ">
            <form>
              <input
                //   onChange={handleSearchPasienRanapChange}
                //   value={searchQueryPasienRanap}
                className="border border-black pl-0.5 py-0.4  "
                type="text"
                placeholder="Cari pasien rawat inap..."
              />
            </form>
          </div>
          <div className="container mx-auto w-full flex justify-end ">
            <button
