import React, { useState } from "react";
import useSWR from "swr";
import axios from "axios";

const fetcher = (url) => axios.get(url).then((res) => res.data);

const SearchWithPagination = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [page, setPage] = useState(0);
  const [limit, setLimit] = useState(0);
  const [searched, setSearched] = useState(false);

  const { data, error } = useSWR(
    searched
      ? `http://localhost:5000/igd/pasien?search=${searchQuery}&page=${page}&limit=5`
      : null,
    fetcher
  );

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the page from refreshing on submit
    setSearched(true);
  };

  if (error) return <div>Error loading data.</div>;
  if (!data && searched) return <div>Loading...</div>;

  console.log("Current page:", page);
  console.log("Data:", data);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search..."
        />
        <button type="submit">Search</button>
      </form>

      {searched && (
        <>
          <ul>
            {data.data.map((item) => (
              <li key={item.id}>{item.nama_lengkap}</li>
            ))}
          </ul>

          <br />
          <br />
          <br />
          <br />
          <button onClick={() => setPage((page) => page - 1)} disabled={page === 0}>
            Previous
          </button>
          <button
            onClick={() => {
              setPage((page) => page + 1);
            }}
            disabled={!data.data || data.data.length === 0 || data.total_page === page + 1}
          >
            Next Page
          </button>
        </>
      )}
    </div>
  );
};

export default SearchWithPagination;
