import React, { useState, useEffect } from "react";

const Wilayah = () => {
  const [provinces, setProvinces] = useState([]);
  const [regencies, setRegencies] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [villages, setVillages] = useState([]);

  const [selectedProvince, setSelectedProvince] = useState("");
  const [selectedRegency, setSelectedRegency] = useState("");
  const [selectedDistrict, setSelectedDistrict] = useState("");
  const [selectedVillage, setSelectedVillage] = useState("");

  useEffect(() => {
    const fetchProvinces = async () => {
      const response = await fetch("https://emsifa.github.io/api-wilayah-indonesia/api/regencies/11");
      const data = await response.json();
      setProvinces(data);
    };
    fetchProvinces();
  }, []);

  // useEffect(() => {
  //   const fetchRegencies = async () => {
  //      console.log(`Fetching regencies for province ${selectedProvince}`);
  //     if (selectedProvince) {
  //       const response = await fetch(`https://emsifa.github.io/api-wilayah-indonesia/api/regencies/${selectedProvince}.json`);
  //       const data = await response.json();
  //       setRegencies(data);
  //     }
  //   };
  //   fetchRegencies();
  // }, [selectedProvince]);

  // useEffect(() => {
  //   const fetchDistricts = async () => {
  //     if (selectedRegency) {
  //       const response = await fetch(`https://emsifa.github.io/api-wilayah-indonesia/api/districts/${selectedRegency}.json`);
  //       const data = await response.json();
  //       setDistricts(data);
  //     }
  //   };
  //   fetchDistricts();
  // }, [selectedRegency]);

  // useEffect(() => {
  //   const fetchVillages = async () => {
  //     if (selectedDistrict) {
  //       const response = await fetch(`https://emsifa.github.io/api-wilayah-indonesia/api/villages/${selectedDistrict}.json`);
  //       const data = await response.json();
  //       setVillages(data);
  //     }
  //   };
  //   fetchVillages();
  // }, [selectedDistrict]);

  const handleProvinceChange = (e) => {
    setSelectedProvince(e.target.value);
    setSelectedRegency("");
    setSelectedDistrict("");
    setSelectedVillage("");

  const fetchRegencies = async () => {
    const response = await fetch(`https://emsifa.github.io/api-wilayah-indonesia/api/regencies/${e.target.value}.json`);
    const data = await response.json();
    setRegencies(data);
  };
  fetchRegencies();
  };

  const handleRegencyChange = (e) => {
    setSelectedRegency(e.target.value);
    setSelectedDistrict("");
    setSelectedVillage("");
  };

  const handleDistrictChange = (e) => {
    setSelectedDistrict(e.target.value);
    setSelectedVillage("");
  };

  const handleVillageChange = (e) => {
    setSelectedVillage(e.target.value);
  };

  return (
    <div>
      <h2>Pilih Wilayah</h2>
      <div>
        <label>Provinsi:</label>
        <select value={selectedProvince} onChange={handleProvinceChange}>
          <option value="">Pilih Provinsi</option>
          {provinces.map((province) => (
            <option key={province.id} value={province.id}>
              {province.id}
            </option>
          ))}
        </select>
      </div>
      {/* {selectedProvince && ( */}
        <div>
          <label>Kabupaten/Kota:</label>
          <select value={selectedRegency} onChange={handleRegencyChange}>
            <option value="">Pilih Kabupaten/Kota</option>
            {regencies.map((regency) => (
              <option key={regency.id} value={regency.id}>
                {regency.name}
              </option>
            ))}
          </select>
        </div>
      {/* )} */}
</div>
  );
};

export default Wilayah;
