import React, { useState, useEffect } from "react";
import { API_URL } from "../../config";
import axios from "axios";
import { Link } from "react-router-dom";
import Modal from "../../component/Modal";
import closeIcon from "../../images/close.png";

const Pendaftaran = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [tampilanSearch, setTampilanSearch] = useState(false);

  const [datapasien, setDataPasien] = useState([]);
  const [formData, setFormData] = useState({});
  const [simpanid, setSimpanId] = useState();

  const [showModal, setShowModal] = useState(false);
  const [isDeleteOpen, setIsDeleteOpen] = useState(false);
  const [deleteUserId, setDeleteUserId] = useState(null);

  const [allpasienigd, setAllPasienIgd] = useState();
  const [listPasienIgdPenanganan, setListPasienIgdPenanganan] = useState();

  useEffect(() => {
    axios
      .get(`${API_URL}/igd/pasien/all`)
      .then((response) => setAllPasienIgd(response.data));
  }, [allpasienigd]);

  useEffect(() => {
    axios
      .get(`${API_URL}/igd/pasien/list/penanganan`)
      .then((response) => setListPasienIgdPenanganan(response.data));
  }, [listPasienIgdPenanganan]);

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleklik = (event, id) => {
    event.preventDefault();
    setSearchResult([]);
    setSearchQuery("");
    setTampilanSearch(true);
    fetch(`${API_URL}/igd/pasien/search/${id}`)
      .then((response) => response.json())
      .then((datak) => {
        setDataPasien([datak]);
        setSimpanId(parseInt(datak.id));
      })
      .catch((error) => console.log(error));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // mengambil data dari API berdasarkan input pencarian pengguna
    fetch(`${API_URL}/igd/pasien?search=${searchQuery}&page=&limit=10`)
      .then((response) => response.json())
      .then((data) => setSearchResult(data.data))
      .catch((error) => console.log(error));
  };

  const handleRegisterPasien = async (event) => {
    event.preventDefault();
    const dataToSend = {
      ...formData,
      id_pasien_rm: simpanid,
    };
    try {
      const response = await axios.post(
        `${API_URL}/igd/pasien/register`,
        dataToSend
      );
      setTampilanSearch(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleFormChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });

    //  const { name, value } = event.target;
    // setEditData(prevState => ({
    //     ...prevState,
    //     [name]: value
    // }));
  };

  // punya delete

  const handleDeleteUser = async (PasienId) => {
    try {
      await axios.delete(`${API_URL}/igd/pasien/${PasienId}`);
    } catch (error) {
      console.error(error);
      alert("Failed to delete user.");
    }
  };

  const handleConfirmDelete = () => {
    handleDeleteUser(deleteUserId);
    setIsDeleteOpen(false);
    setDeleteUserId(null);
  };

  const handleShowModal = (PasienId) => {
    setDeleteUserId(PasienId);
    setIsDeleteOpen(true);
  };

  const handleCloseModal = () => {
    setIsDeleteOpen(false);
  };

  useEffect(() => {
    if (searchQuery !== "") {
      fetch(`${API_URL}/igd/pasien?search=${searchQuery}&page=&limit=5`)
        .then((response) => response.json())
        .then((data) => setSearchResult(data.data))
        .catch((error) => console.error(error));
    } else {
      setSearchResult([]);
    }
  }, [searchQuery]);

  const handleCloseTampilan = () => {
    setTampilanSearch(false);
  };

  return (
    <>
      <div className="container border border-state-300 bg-white p-5 mt-5 mb-4 ">
        <div className="search text-left flex">
          <form onSubmit={handleSubmit}>
            <input
              className="border border-black pl-0.5 py-0.4  "
              type="text"
              value={searchQuery}
              onChange={handleInputChange}
              placeholder="Search..."
              autoFocus
            />
            <button className="ml-1 py-0.2 px-1 bg-emerald text-white rounded hover:opacity-75">
              Submit
            </button>
          </form>
        </div>

        <ul className="text-left">
          {searchResult.map((item) => (
            <li key={item.id}>
              <div className="container w-30 bg-white border border-state-300">
                <a
                  href="#"
                  className="hover:bg-grey-500 pr-5 pl-1 py-1"
                  onClick={(event) => handleklik(event, item.id)}
                >
                  <span className="font-sm"> {item.nama_lengkap} </span>
                </a>
              </div>
            </li>
          ))}
        </ul>

        {tampilanSearch && (
          <div className="container mx-auto">
            <div className="flex mt-1 mb-1">
              <div className="ml-auto">
                <a href="#" onClick={handleCloseTampilan}>
                  <img src={closeIcon} width="20px" />
                </a>
              </div>
            </div>
            <div className="flex">
              <div className="container border border-state-300 w-2/3 mr-5 p-2">
                {datapasien.map((pasien) => (
                  <div className="text-left" key={pasien.id}>
                    <p>no rm :{pasien.no_rm}</p>
                    <p>nik :{pasien.no_kitas}</p>
                    <p>nama :{pasien.nama_lengkap}</p>
                    <p>tanggal lahir :{pasien.tanggal_lahir}</p>
                    <p>kelamin :{pasien.kelamin}</p>
                  </div>
                ))}
              </div>
              <div className="container border border-state-300 w-2/3">
                <form onSubmit={handleRegisterPasien}>
                  {/* <input type="text" name='id_pasien_rm' value={simpanid} onChange={handleFormChange} className='border border-black ml-1 pl-0.5 py-0.4'/> */}
                  <div className="container text-left ml-2 mt-4 mr-0">
                    <tr>
                      <td className="pr-1 ml-1">Tanggal Masuk</td>
                      <td className="pb-1">
                        <input
                          type="text"
                          name="tgl_masuk"
                          onChange={handleFormChange}
                          className="border border-black ml-1 mx-32 pr-0.5 pl-0.5 py-0.4 w-full"
                        />
                      </td>
                    </tr>
                    <tr>
                      <td className="pr-1">Jam Masuk</td>
                      <td className="pb-1">
                        <input
                          type="text"
                          name="jam_masuk"
                          onChange={handleFormChange}
                          className="border border-black ml-1 mx-28  pl-0.5 py-0.4 w-full"
                        />
                      </td>
                    </tr>
                    <tr>
                      <td className="pr-1">Cara Masuk</td>
                      <td className="pb-1">
                        <input
                          type="text"
                          name="cara_masuk"
                          onChange={handleFormChange}
                          className="border border-black mx-28 ml-1 pl-0.5 py-0.4 w-full"
                        />
                      </td>
                    </tr>
                    <tr>
                      <td className="pr-1">Pembayaran</td>
                      <td className="pb-1">
                        <input
                          type="text"
                          name="pembayaran_igd"
                          onChange={handleFormChange}
                          className="border border-black mx-28 ml-1 pl-0.5 py-0.4 w-full"
                        />
                      </td>
                    </tr>
                  </div>
                  <div className="flex justify-end pb-1 pt-1">
                    <button className="ml-1 mr-2 mb-2 py-0.2 px-1 bg-emerald text-white rounded hover:opacity-75">
                      Submit
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}

        <div class="overflow-x-auto mb-5">
          <div className="container bg-emerald300 text-left pl-2 mt-3 py-0.5">
            Daftar Antrian Pasien IGD
          </div>
          <table class="table-auto w-full">
            <thead>
              <tr>
                <th class="px-6 py-3 bg-gray-50 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  No
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Tanggal Masuk
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Jam
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  No RM
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Nama Pasien
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Cara Masuk
                </th>
                <th class="px-6 py-3 bg-gray-50 text-left text-xs fonst-medium text-gray-500 uppercase tracking-wider"></th>
              </tr>
            </thead>
            <tbody class="bg-white divide-y divide-gray-200">
              {allpasienigd && allpasienigd.length > 0 ? (
                allpasienigd.map((pasien, index) => (
                  <tr key={pasien.id}>
                    <td class=" py-0.3 px-6 text-left whitespace-nowrap">
                      {index + 1}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.tgl_masuk}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.jam_masuk}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.pasien_rm.no_rm}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.pasien_rm.nama_lengkap}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.cara_masuk}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      <Link
                        to={`/igd/pasien/tangani/${pasien.id}`}
                        className="ml-1 py-0.1 my-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
                        type="button"
                      >
                        Tangani
                      </Link>
                      <button
                        onClick={() => handleShowModal(pasien.id)}
                        className="ml-1 py-0.1 px-1 mr-1 my-0.2 bg-red-600 text-white  hover:opacity-75"
                        type="button"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colspan="7" className="bg-gray-200 w-full">
                    Belum ada pasien dalam antrian IGD
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <div class="overflow-x-auto my-5 mt-5">
          <div className="container bg-emerald300 text-left pl-2 mt-3 py-0.5">
            List Pasien dalam Penanganan Pasien IGD
          </div>
          <table class="table-auto w-full">
            <thead>
              <tr>
                <th class="px-6 py-3 bg-gray-50 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  No
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Tanggal Masuk
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Jam
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  No RM
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Nama Pasien
                </th>
                <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status Pasien
                </th>
                <th class="px-6 py-3 bg-gray-50 text-left text-xs fonst-medium text-gray-500 uppercase tracking-wider"></th>
              </tr>
            </thead>
            <tbody class="bg-white divide-y px-6 divide-gray-200">
              {listPasienIgdPenanganan && listPasienIgdPenanganan.length > 0 ? (
                listPasienIgdPenanganan.map((pasien, index) => (
                  <tr key={pasien.id}>
                    <td class=" py-0.3 text-left whitespace-nowrap">
                      {index + 1}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.tgl_masuk}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.jam_masuk}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.pasien_rm.no_rm}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      {pasien.pasien_rm.nama_lengkap}
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      <span
                        className={`font-bold ${
                          pasien.triase_ats_pasien_igd[0].plan === "Zona Merah"
                            ? " text-red-600"
                            : pasien.triase_ats_pasien_igd[0].plan ===
                              "Zona Kuning"
                            ? "text-orange-600"
                            : pasien.triase_ats_pasien_igd[0].plan ===
                              "Zona Hijau"
                            ? "text-green-600"
                            : ""
                        }`}
                      >
                        {pasien.triase_ats_pasien_igd[0].plan}
                      </span>
                    </td>
                    <td class=" py-0.3 whitespace-nowrap">
                      <Link
                        to={`/igd/pasien/tangani/${pasien.id}`}
                        className="ml-1 py-0.1 my-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
                        type="button"
                      >
                        Detail
                      </Link>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colspan="7" className="bg-gray-200 w-full">
                    Belum ada pasien dalam daftar penanganan
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* modal delete */}
      <Modal isOpen={isDeleteOpen} onClose={handleCloseModal}>
        <div class="bg-white rounded-lg w-1/3 mt-10 overflow-hidden shadow-xl transform transition-all max-w-screen-lg ">
          <div class="bg-gray-50 p-6">
            <div class="flex flex-col md:flex-row">
              <div class="w-full">
                <h2 class="text-xl font-bold py-4 ">Are you sure?</h2>
                <p class="text-sm text-gray-500 px-8">
                  Do you really want to delete this data? This process cannot be
                  undone
                </p>
                <div className="mt-4">
                  <button
                    onClick={handleConfirmDelete}
                    class="px-4 py-2 mr-2 bg-red-800 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-600 focus:ring-opacity-50"
                  >
                    Yes
                  </button>
                  <button
                    onClick={() => setIsDeleteOpen(false)}
                    class="px-4 py-2 ml-2 bg-gray-100 text-state-700 border border-black rounded-md hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-600 focus:ring-opacity-50 mr-2"
                  >
                    No
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Modal>
    </>
  );
};

export default Pendaftaran;
