                <option value="" className='py-5'>Pilih Pekerjaan</option>
                {pekerjaan.map(option => (
                <option key={option.id} value={option.nama_pekerjaan}>{option.nama_pekerjaan}</option>
                ))}
            </select>
            </div>
         </div>
        </div>
           
   

        <div class="w-full md:w-1/2 px-4">
