                <label class="block text-gray-700 text-sm text-left font-bold mb-2">Kitas</label>
                <input class="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                 type="text" name='no_kitas' placeholder="Masukkan Nomor Indentitas" onChange={handleFormChange} value={editData.no_kitas} onClick={(e) => e.stopPropagation()} />
            </div>

            <div className='field my-2 flex'>
                <div class="flex items-center mr-2">
                    <input 
                        id="kitas1"
                        name="kitas"
                        type="radio"
                        checked={editKitas.kitas === 'KTP'}
                        class="form-radio h-4 w-4 text-indigo-600" 
                        value="KTP"
                        onChange={handleOptionKitasChange}
                      
                    />
                    <label for="kitas1" class="ml-1 text-gray-700">KTP</label>
                </div>
                <div class="flex items-center">
                    <input
                        id="kitas2"
                        name="kitas"
                        type="radio"
                        checked={editKitas.kitas === 'Paspor'}
                        class="form-radio h-4 w-4 text-indigo-600"
                        onChange={handleOptionKitasChange}
                        value="Paspor"
                    />
                    <label for="kitas2" class="ml-1 text-gray-700">Paspor</label>
