import React, { useState, useEffect } from "react";
import axios from "axios";
import { API_URL } from "../../../../config";

const TambahPenjualan = () => {
  const [rowData, setRowData] = useState([
    { id_obat: "" },
    { jumlah: "" },
    { harga_satuan: "" },
    { diskon: "" },
    { total_harga: "" },
    { tanggal_penjualan: "" },
    { informasi_pembayaran: "" },
    { metode_pembayaran: "" },
  ]);

  const addRow = () => {
    let newRow = { id_obat: "" };
    setRowData([...rowData, newRow]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = rowData.map((row) => ({
        id_obat: Number(row.id_obat),
      }));

      const sendData = await axios.post(
        `${API_URL}/farmasi/obat/penjualan`,
        data
      );
    } catch (error) {
      console.log(error);
    }
  };

  const handleFormChange = (index, event) => {
    let data = [...rowData];
    data[index][event.target.name] = Number(event.target.value);
    setRowData(data);
  };

  return (
    <div>
      <div className="container bg-emerald300 text-left pl-2 mt-3 py-0.5">
        <div className="flex justify-between px-4">
          <p>Input Penjualan</p>
          <button onClick={addRow}>Add Rows</button>
        </div>
      </div>
      <div className="overflow-y-auto max-h-[48vh]">
        <form onSubmit={handleSubmit}>
          {rowData.map((input, index) => {
            return (
              <div key={index}>
                <input
                  className="border border-black"
                  name="id_obat"
                  value={input.id_obat}
                  onChange={(event) => handleFormChange(index, event)}
                />
              </div>
            );
          })}

          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default TambahPenjualan;
