import React, { useState, useEffect } from "react";
import Sidebar from "../../templates/sidebar";
import Header from "../../templates/header";
import { useNavigate } from "react-router-dom";
import Modal from "../../../component/Modal";
import { API_URL } from "../../../config";
import axios from "axios";
import closeIcon from "../../../images/close.png";
import { Link, Route, Routes } from "react-router-dom";

const DataObat = () => {
  const navigate = useNavigate();
  const handleBack = () => {
    navigate(-1);
  };
  // pagination
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(10);
  const [totalPages, setTotalPages] = useState();
  const [dataObat, setDataObat] = useState({});

  // search
  const [searchQueryDataObat, setsearchQueryDataObat] = useState("");

  //all pasien ranap
  useEffect(() => {
    const fetchAllDataObat = async () => {
      try {
        if (searchQueryDataObat !== "") {
          const response = await axios.get(
            `${API_URL}/farmasi/obat?search=${searchQueryDataObat}&page=1&limit=${limit}`
          );
          setDataObat(response.data.data);
          setTotalPages(response.data.totalPages);
        } else {
          const response = await axios.get(
            `${API_URL}/farmasi/obat?search=${searchQueryDataObat}&page=${page}&limit=${limit}`
          );
          setDataObat(response.data.data);
          setTotalPages(response.data.totalPages);
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchAllDataObat();
  }, [searchQueryDataObat, page, dataObat, limit]);

  // console.log(dataObat);

  const handleSearchDataObatChange = (event) => {
    const data = event.target.value;
    setsearchQueryDataObat(data);
  };

  return (
    <div class="h-full">
      <div class="flex">
        <div class="flex-none">
          <Sidebar />
        </div>
        <div class="flex-auto bg-dasar border-l-2 border-opacity-30 border-gray-300 shadow-md">
          <Header />
          <div className="flex justify-between my-3 mx-8">
            <button
              className="py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
              type="button"
              onClick={handleBack}
            >
              KEMBALI
            </button>
            <div className="flex">
              <Link to={"/farmasi/penjualan"}>
                <button
                  className="py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
                  type="button"
                >
                  PENJUALAN
                </button>
              </Link>
              <Link to={"/farmasi/resep"}>
                <button
                  className="py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
                  type="button"
                >
                  RESEP
                </button>
              </Link>
              <Link to={"/farmasi/gudang"}>
                <button
                  className="py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
                  type="button"
                >
                  GUDANG
                </button>
              </Link>
              <Link to={"/farmasi/report"}>
                <button
                  className="py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75"
                  type="button"
                >
                  REPORT
                </button>
              </Link>
            </div>
          </div>

          <div class="container mx-auto 2xl:w-screen px-8 pb-10">
            <div className="container border border-state-300 bg-white p-2 mt-5 ">
              <div className="flex justify-end mb-4">
                <Link to={"/farmasi/obat/data"}>
                  <button
                    className="py-0.2 text-sm  px-1 mr-1 bg-white border text-black border-black  hover:bg-emerald300"
                    type="button"
                  >
                    DATA OBAT
                  </button>
                </Link>
                <Link to={"/farmasi/resep"}>
                  <button
                    className="py-0.2 text-sm  px-1 mr-1 bg-white border text-black border-black  hover:bg-emerald300"
                    type="button"
                  >
                    STOK OBAT
                  </button>
                </Link>
                <Link to={"/farmasi/gudang"}>
                  <button
                    className="py-0.2 text-sm  px-1 mr-1 bg-white border text-black border-black hover:bg-emerald300"
                    type="button"
                  >
                    LAPORAN
                  </button>
                </Link>
                <Link to={"/farmasi/report"}>
                  <button
                    className="py-0.2 text-sm px-1 mr-1 bg-white border text-black border-black hover:bg-emerald300"
                    type="button"
                  >
                    PEMBELIAN
                  </button>
                </Link>
              </div>
              <div className="search text-left flex">
                <div className="search text-left flex ">
                  <form>
                    <input
                      onChange={handleSearchDataObatChange}
                      value={searchQueryDataObat}
                      className="border border-black pl-0.5 py-0.4  "
                      type="text"
                      placeholder="Cari data obat..."
                    />
                  </form>
                </div>
                <div className="container mx-auto w-full flex justify-end ">
                  <button
                    // onClick={() => setPage((page) => page - 1)}
                    // hidden={page === 1}
                    type="button"
                    class="bg-white text-state-400 rounded-l-md border border-black border-l  mr-1"
                  >
                    <div class="flex flex-row align-middle">
                      <svg
                        class="w-4 mr-1 mt-0.5"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          fill-rule="evenodd"
                          d="M7.707 14.707a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l2.293 2.293a1 1 0 010 1.414z"
                          clip-rule="evenodd"
                        ></path>
                      </svg>
                      <p class="ml-0.5 mr-1 text-sm">Prev</p>
                    </div>
                  </button>

                  <button
                    // onClick={() => {
                    //   setPage((page) => page + 1);
                    // }}
                    // hidden={page === totalPages}
                    type="button"
                    class="bg-white text-state-400 border border-black rounded-r-md border-l "
                  >
                    <div class="flex flex-row align-middle">
                      <p class="mr-0.5 ml-0.5 text-sm">Next</p>
                      <svg
                        class="w-4 mr-1 mt-0.5"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          fill-rule="evenodd"
                          d="M12.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-2.293-2.293a1 1 0 010-1.414z"
                          clip-rule="evenodd"
                        ></path>
                      </svg>
                    </div>
                  </button>
                </div>
              </div>
              <div class="overflow-x-auto mb-5 ">
                <div className="container bg-emerald300 text-left pl-2 mt-3 py-0.5">
                  Data Obat
                </div>
                <div className="overflow-y-auto max-h-[48vh]">
                  <table class="table-auto w-full">
                    <thead className="sticky top-0">
                      <tr>
                        <th class="px-6 py-3 bg-gray-50  text-xs font-medium text-gray-500 uppercase tracking-wider">
                          No
                        </th>
                        <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Nama Obat
                        </th>
                        <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Kategori
                        </th>
                        <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Deskripsi Obat
                        </th>
                        <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Dosis Obat
                        </th>
                        <th class="px-6 py-3 bg-gray-50 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Aksi
                        </th>
                        <th class="px-6 py-3 bg-gray-50 text-left text-xs fonst-medium text-gray-500 uppercase tracking-wider"></th>
                      </tr>
                    </thead>
                    <tbody class="bg-white divide-y divide-gray-200 ">
                      {dataObat.length > 0 ? (
                        dataObat.map((item, index) => (
                          <tr key={item.id}>
                            <td className="class=py-0.3 px-6 whitespace-nowrap">
                              {index + 1}
                            </td>
                            <td className="class=py-0.3 px-6 whitespace-nowrap">
                              {item.nama_obat}
                            </td>
                            <td className="class=py-0.3 px-6 whitespace-nowrap">
                              {item.kategori_obat}
                            </td>
                            <td className="class=py-0.3 px-6 whitespace-nowrap">
                              {item.deskripsi_obat}
                            </td>
                            <td className="class=py-0.3 px-6 whitespace-nowrap">
                              {item.id}
                            </td>
                            <td className="class=py-0.3 px-6 whitespace-nowrap">
                              {item.id}
                            </td>
                          </tr>
                        ))
                      ) : (
                        <div>kkm</div>
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DataObat;
