import React, { useState } from "react";
import useSWR from "swr";
import axios from "axios";

const fetcher = (url) => axios.get(url).then((res) => res.data);

const SearchWithPagination = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [page, setPage] = useState(0);
  const [limit, setLimit] = useState(0);
  const [isSearchTriggered, setIsSearchTriggered] = useState(false);
  const { data, error } = useSWR(
    `http://localhost:5000/igd/pasien?search=${searchQuery}&page=${page}&limit=5`,
    fetcher,
    { shouldRetryOnError: false }
  );

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the page from refreshing on submit

    // Set the search trigger flag to true to indicate that the search button was clicked
    setIsSearchTriggered(true);
  };

  // If the search button was clicked, update the data query with the latest search query
  const dataQuery = isSearchTriggered
    ? `http://localhost:5000/igd/pasien?search=${searchQuery}&page=${page}&limit=5`
    : `http://localhost:5000/igd/pasien?page=${page}&limit=5`;

  const { data: searchData, error: searchError } = useSWR(
    dataQuery,
    fetcher,
    { shouldRetryOnError: false }
  );

  if (searchError) return <div>Error loading data.</div>;
  if (!searchData) return <div>Loading...</div>;

  console.log("Current page:", page);
  console.log("Data:", searchData);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search..."
        />
        <button type="submit">Search</button>
      </form>

      <ul>
        {searchData.data.map((item) => (
          <li key={item.id}>{item.nama_lengkap}</li>
        ))}
      </ul>

      <br />
      <br />
      <br />
      <br />
      <button onClick={() => setPage((page) => page - 1)} disabled={page === 0}>
        Previous
      </button>
      <button
        onClick={() => {
          setPage((page) => page + 1);
          // Reset the search trigger flag when navigating to the next page
          setIsSearchTriggered(false);
        }}
        disabled={searchData.data.length < 5}
      >
        Next Page
      </button>
    </div>
  );
};

export default SearchWithPagination;
