import React, {useState, useEffect} from 'react'

const Pendaftaran = () => {
  const [searchQuery, setSearchQuery] = useState('');
   const [searchResult, setSearchResult] = useState([]);
 
   const [livesearchQuery, setLiveSearchQuery] = useState('');
   const [livesearchResult, setLiveSearchResult] = useState([]);

     const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

   const handleSubmit = async (event) => {
        event.preventDefault();
        // mengambil data dari API berdasarkan input pencarian pengguna
        fetch(`http://localhost:5000/igd/pasien?search=${searchQuery}&page=1&limit=1`)
      .then((response) => response.json())
      .then((data) => setSearchResult(data.data))
      .catch((error) => console.log(error));
    
    };

     fetch('http://localhost:5000/pendidikan')
        .then(response => response.json())
        .then(data => setPendidikan(data))


    useEffect(() => {
     fetch(`http://localhost:5000/igd/pasien?search=${searchQuery}&page=1&limit=5`)
      .then(response => response.json())
        .then(data => setLiveSearchResult(data))
    // const results = myData.filter(item => item.title.includes(livesearchQuery));
    // setLiveSearchResult(results);
  }, [livesearchQuery]);
    

  
  return (
    <>
    
     <div className="container border border-state-300 bg-white p-5">
    <div className='search text-left flex'>
        <form onSubmit={handleSubmit}>
            <input className='border border-black pl-0.5 py-0.4  ' type="text" value={searchQuery} onChange={handleInputChange} placeholder="Search..." autoFocus/>
           <button className="ml-1 py-0.2 px-1 bg-emerald text-white rounded hover:opacity-75"  >
                Submit
           </button>
        </form>
    </div>

         <ul className='text-left mt-5'>
        {searchResult.map((item) => (
          <li key={item.id}>
           <p>no rm :{item.no_rm}</p> 
            <p>no kitas: {item.no_kitas}</p>
            <p>nama lengkap : {item.nama_lengkap}</p>
            <p>kelamin : {item.kelamin}</p>
            <p>telpon : {item.kontak_pasien}</p>
            <p>alamat : {item.alamat_pasien_detail}</p>
        </li>
  
        ))}
      </ul>
    </div>


    <br/>
    <br/>
    <br/>

    live search 

     <div>
      <input type="text" value={livesearchQuery} onChange={e => setLiveSearchQuery(e.target.value)} />
      <ul>
        {livesearchResult.map(item => (
          <li key={item.id}>{item.nama_lengkap}</li>
        ))}
      </ul>
    </div>
        
    
    </>
  )
}

export default Pendaftaran
