import React, {useState, useEffect} from 'react'
import axios from "axios";
import { Link, useNavigate , useParams} from "react-router-dom";


const Triase = () => {
    const {id} = useParams();
    const [pasienigdtriase, setPasienIgdTriase] = useState([]);
    const nilai = Number(id);

    
    function fetchTriaseIgd(id) {
      axios
      .get(`http://localhost:5000/igd/pasien/penanganan/triase/${id}`)
       .then(response => {
      const data = response.data; 
      setPasienIgdTriase(data); 

      })
      .catch((error) => {
        console.error(error);
      });
    }

    
    useEffect(() => {
      fetchTriaseIgd(id);
    }, [id]);
    
    // menangkap nilai array
    const IdPasienIgd = pasienigdtriase.map(item => item.id_pasien_igd);
    // parse to number
    const nilaiIdPasienIgd = Number(IdPasienIgd);


  function handleKeluhan(event) {
  event.preventDefault();
  const formData = new FormData(event.target);
  const newKeluhan = formData.get('keluhan');

  axios.post(`http://localhost:5000/igd/pasien/penanganan/triase`, {
    keluhan_utama: newKeluhan
  })
  .then(response => {
    const updatedData = response.data;
    setPasienIgdTriase(updatedData);
  })
  .catch((error) => {
    console.error(error);
  });
}
    
  return (
<>


    <div className='container text-left'>
            { nilai === nilaiIdPasienIgd  ? (
                <div className='keluhan'>
                    {pasienigdtriase && pasienigdtriase.map(data => (
                      <div key={data.id}>
                        Keluhan: {data.keluhan_utama}
                      </div>
                    ))}
                </div>
            ) : (
                <form onSubmit={handleKeluhan}>
                    Keluhan : <input type="text" className='border border-black' />
                    <button className='ml-1 py-0.2 px-1 mr-1 bg-emerald text-white  hover:opacity-75' type='button'>SIMPAN</button>
                </form>
            )}
    </div>
</>
  )
}

export default Triase
