import React, {useState, useEffect} from 'react'
import axios from "axios";
import useSWR from 'swr';
import { useSWRConfig } from 'swr';
import Modal from '../../component/Modal';
// import {Link} from "react-router-dom";

const Pasien = () => {
    
    const [isOpen, setIsOpen] = useState(false);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [formData, setFormData] = useState({});
    const [editData, setEditData] = useState({ nama_lengkap: '' });
    
    // delete
    const [deleteUserId, setDeleteUserId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    
    // search and pagination state
    const [searchQuery, setSearchQuery] = useState("");
    const [page, setPage] = useState(0);
    const [limit, setLimit] = useState(5);
    const [submittedQuery, setSubmittedQuery] = useState(null);
    const [totalRows, setTotalRows] = useState(0);
    const [totalPage, setTotalPage] = useState(0);


    const fetcher = (url) => axios.get(url).then((res) => res.data);
    const { data, error } = useSWR(
        `http://localhost:5000/igd/pasien?search=${searchQuery}&page=${page}&limit=${limit}`,
        fetcher
    );



     const {mutate} = useSWRConfig();

  
       
    if (error) return <div>Error loading data.</div>;
    if (!data) return <div>Loading...</div>;

    // kode toal page
    // const handleTotalPage = () {
    //     const pageNow = 
    //     setTotalPage(pageNow);

    // }


    // kode delete user

    const handleDeleteUser = async (userId) => {
 
    try {
      await axios.delete(`http://localhost:5000/rm/${userId}`);
      mutate("pasien");

    } catch (error) {
      console.error(error);
      alert("Failed to delete user.");
    }
  
};

    const handleConfirmDelete = () => {
    handleDeleteUser(deleteUserId);
    setShowModal(false);
    setDeleteUserId(null);
    };

    const handleShowModal = (userId) => {
    setDeleteUserId(userId);
    setShowModal(true);
    };


 // kode delete user





    // handle submit data
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/rm', formData);
            // console.log(response.data);
            setIsOpen(false);
            mutate("pasien");
        } catch (error) {
            console.error(error);
        }
    };

    // handle change data
    const handleFormChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });

     const { name, value } = event.target;
    setEditData(prevState => ({
        ...prevState,
        [name]: value
    }));

    };


    // milik edit

    // edit data
    const handleEdit = (data) => {
        setEditData(data);
        setIsEditOpen(true);
    };


    // mengirim request ke API untuk mengedit data
    const handleEditSubmit = async (event) => {
    event.preventDefault();
        try {
            const response = await axios.patch(`http://localhost:5000/rm/${editData.id}`, editData);
            // console.log(response.data);
            setIsEditOpen(false);
        mutate("pasien");
        } catch (error) {
            console.error(error);
        }
    };

    // punya modal

    const handleCloseModal = () => {
        setIsOpen(false);
        setIsEditOpen(false);
    };
    

  return (
    <div className="pasien">
        <div className='judul'>
            <h1 className='text-2xl text-left text-state-600 font-semibold'>Data Rekam Medis Pasien</h1>


            <button onClick={() => setIsOpen(true)} className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded '>New Pasien</button>
            
            {/* modal */}

            <Modal isOpen={isOpen} onClose={handleCloseModal}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                    Tambah Data Pasien
                </h3>
                <div className="mt-2">
                     <form onSubmit={handleSubmit}>
                        <input type="text" autoFocus name="nama_lengkap" className='block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring' onChange={handleFormChange} />
                        <button type="submit" className='px-6 py-2 leading-5 text-white transition-colors duration-200 transform bg-gray-700 rounded-md hover:bg-gray-600 focus:outline-none focus:bg-gray-600 mt-5'>Submit</button>
                    </form>

                    <br/>
                    <br/>
                    <br/>

<section class="max-w-4xl p-6 mx-auto bg-white rounded-md shadow-md dark:bg-gray-800 mt-20">
    <h2 class="text-lg font-semibold text-gray-700 capitalize dark:text-white">Account settings</h2>
    
    <form>
        <div class="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2">
            <div>
                <label class="text-gray-700 dark:text-gray-200" for="username">Username</label>
                <input  type="text" className='block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring' />
            </div>

            <div>
                <label class="text-gray-700 dark:text-gray-200" for="emailAddress">Email Address</label>
                <input id="emailAddress" type="email" class="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" />
            </div>

            <div>
                <label class="text-gray-700 dark:text-gray-200" for="password">Password</label>
                <input id="password" type="password" class="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" />
            </div>

            <div>
                <label class="text-gray-700 dark:text-gray-200" for="passwordConfirmation">Password Confirmation</label>
                <input id="passwordConfirmation" type="password" class="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" />
            </div>
        </div>

        <div class="flex justify-end mt-6">
            <button class="px-6 py-2 leading-5 text-white transition-colors duration-200 transform bg-gray-700 rounded-md hover:bg-gray-600 focus:outline-none focus:bg-gray-600">Save</button>
        </div>
    </form>
</section>
                </div>
                </div>
            </Modal>

            {/* modal */}


            {/* modal edit */}
           
           <Modal isOpen={isEditOpen} onClose={handleCloseModal}>
        <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
            <h3 className="text-lg leading-6 font-medium text-gray-900">
            Edit Data
            </h3>
            <div className="mt-2">
            <form onSubmit={handleEditSubmit}>
                <label>Nama Lengkap:</label><br/>
                <input type="text" name="nama_lengkap" value={editData.nama_lengkap} onChange={handleFormChange} onClick={(e) => e.stopPropagation()} /> <br/>
                <button type="submit">Update</button>
            </form>
            </div>
        </div>
        </Modal>

           
            {/* modal edit */}


        {/* model delete */}
        {showModal && (
        <div>
            <p>Are you sure you want to delete this user?</p>
            <button onClick={handleConfirmDelete}>Yes</button>
            <button onClick={() => setShowModal(false)}>No</button>
        </div>
        )}
           

<div className='container mx-auto w-full mt-1 flex justify-between'>
                <form >
                    <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Search..." autoFocus
                    />
                </form>


    {/* prev and next page */}
<div className='container mx-auto w-full mt-1 flex justify-end'>
    <button onClick={() => setPage((page) => page - 1)} disabled={page === 0} type="button" class="bg-white text-state-400 rounded-l-md border border-black border-l border-gray-200 mr-1">
    <div class="flex flex-row align-middle">
        <svg class="w-4 mr-1 mt-0.5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
        <path fill-rule="evenodd" d="M7.707 14.707a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l2.293 2.293a1 1 0 010 1.414z" clip-rule="evenodd"></path>
        </svg>
        <p class="ml-0.5 mr-1 text-sm">Prev</p>
    </div>
    </button>

    <button onClick={() => {setPage((page) => page + 1);}} disabled={page === data.totalPages-1}  type="button" class="bg-white text-state-400 border border-black rounded-r-md border-l border-gray-200 ">
      <div class="flex flex-row align-middle">
        <p class="mr-0.5 ml-0.5 text-sm">Next</p>
        <svg class="w-4 mr-1 mt-0.5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
          <path fill-rule="evenodd" d="M12.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-2.293-2.293a1 1 0 010-1.414z" clip-rule="evenodd"></path>
        </svg>
      </div>
    </button>
</div>
</div>


            
            <div className="container mx-auto mt-2 py-4  bg-white">
                <table className="min-w-full divide-y divide-gray-200">
                    <thead>
                        <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">No RM</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Nama</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Kitas</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Kelamin</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal Lahir</th>
                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                    </thead>
                    
                    <tbody className="bg-white divide-y divide-gray-200">
                        {data.data.map((pasien, index) => (

                        <tr key={pasien.id}>

                        <td className="px-0 py-1 whitespace-nowrap">
                            <div className="text-sm text-gray-900"> {pasien.no_rm}</div>
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.nama_lengkap}
                        </td>
                        <td className="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.no_kitas}
                        </td>
                        <td className="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.kelamin}
                        </td>
                        <td className="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.tanggal_lahir}
                        </td>
            
                        <td className="px-0 py-0 whitespace-nowrap text-center text-sm font-medium">
                            <button onClick={() => handleEdit(pasien)}  className="text-indigo-600 hover:text-indigo-900">Edit</button>
                            {/* <button onClick={()=> deleteUser(pasien.id)} className="ml-4 text-red-600 hover:text-red-900">Delete</button> */}
                             <button  onClick={() => handleShowModal(pasien.id)} className="ml-4 text-red-600 hover:text-red-900">Delete</button>
                        </td>
                        </tr>
                  
                    ))}
                    
                    </tbody>

                        <div>
        <p>Jumlah Data: {data.totalRows}</p>
        <p>Halaman: {data.totalPages}</p>
      </div>

                 
                </table>

              

            </div>
        </div>
    </div>
  )
}

export default Pasien
