import React, {useState, useEffect} from 'react'

const Pendaftaran = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  
  const [datapasien, setDataPasien] = useState([]);
  const [iddatapasienid, setIdDataPasien] = useState([]);


    const handleInputChange = (event) => {
      setSearchQuery(event.target.value);
    };

    const handleklik = (event, id) => {
     event.preventDefault();
     setSearchResult([]); 
     setSearchQuery('');

      fetch(`http://localhost:5000/igd/pasien/${id}`)
      .then((response) => response.json())
      .then((data) => setDataPasien(data.data))
      .catch((error) => console.log(error));

      console.log(datapasien);
    
  };

   const handleSubmit = async (event) => {
        event.preventDefault();
        // mengambil data dari API berdasarkan input pencarian pengguna
        fetch(`http://localhost:5000/igd/pasien?search=${searchQuery}&page=&limit=10`)
      .then((response) => response.json())
      .then((data) => setSearchResult(data.data))
      .catch((error) => console.log(error));

    
    };


     useEffect(() => {
    if (searchQuery !== '') {
    
      fetch(`http://localhost:5000/igd/pasien?search=${searchQuery}&page=&limit=5`)
        .then(response => response.json())
        .then(data => setSearchResult(data.data))
        .catch(error => console.error(error));
    } else {
      setSearchResult([]);
    }
  }, [searchQuery]);


  //    useEffect(() => {
  //   if (livesearchQuery !== '') {
    
  //     fetch(`http://localhost:5000/igd/pasien?search=${livesearchQuery}&page=&limit=5`)
  //       .then(response => response.json())
  //       .then(data => setLiveSearchResult(data.data))
  //       .catch(error => console.error(error));
  //   } else {
  //     setLiveSearchResult([]);
  //   }
  // }, [livesearchQuery]);

  //   const handleLiveSearchQueryChange = event => {
  //   setLiveSearchQuery(event.target.value);
  // };
    

  
  return (
    <>
    
     <div className="container border border-state-300 bg-white p-5">
    <div className='search text-left flex'>
        <form onSubmit={handleSubmit}>
            <input className='border border-black pl-0.5 py-0.4  ' type="text" value={searchQuery} onChange={handleInputChange} placeholder="Search..." autoFocus/>
           <button className="ml-1 py-0.2 px-1 bg-emerald text-white rounded hover:opacity-75"  >
                Submit
           </button>
        </form>
    </div>

  

      <ul className='text-left mt-5'>
        {searchResult.map((item) => (
          <li key={item.id}>
          <a href='' className='hover:bg-slate-700' onClick={(event) => handleklik(event, item.id)}>  <p>nama lengkap : {item.nama_lengkap}</p> </a>
        </li>
  
        ))}
      </ul>
   
   
   
    </div>

    <div className='container'>
      {datapasien.map((pasien) => (
        <li key={pasien.id}>{pasien.nama_lengkap}</li>
      ))}
    </div>

{/* 
    <br/>
    <br/>
    <br/>

    live search 

     <div>
      <input type="text" value={searchQuery} onChange={handleInputChange} />
      <ul>
        {searchResult.map(data => (
          <li key={data.id}>
            {data.nama_lengkap}
            {data.id}
          
          </li>
        ))}
      </ul>
    </div> */}


    
    </>
  )
}

export default Pendaftaran
