import React, {useState} from 'react'
import { provinsi } from 'wilayah-indonesia';
import { kabupaten } from 'wilayah-indonesia';
import { kecamatan } from 'wilayah-indonesia';
import { desa } from 'wilayah-indonesia';



const contohwilayah = () => {

  state = {
  selectedProvince: '',
  selectedRegency: '',
  selectedDistrict: '',
  selectedVillage: ''
};

handleProvinceSelect = event => {
  this.setState({ selectedProvince: event.target.value });
};

handleRegencySelect = event => {
  this.setState({ selectedRegency: event.target.value });
};

handleDistrictSelect = event => {
  this.setState({ selectedDistrict: event.target.value });
};

handleVillageSelect = event => {
  this.setState({ selectedVillage: event.target.value });
};

  return (
    <div>
         
      <select value={this.state.selectedProvince} onChange={this.handleProvinceSelect}>
        <option value="">Select Province</option>
        {provinsi.map(prov => (
          <option key={prov.id} value={prov.nama}>{prov.nama}</option>
        ))}
      </select>

      <select value={this.state.selectedRegency} onChange={this.handleRegencySelect}>
        <option value="">Select Regency</option>
        {kabupaten.filter(kab => kab.provinsi === this.state.selectedProvince).map(kab => (
          <option key={kab.id} value={kab.nama}>{kab.nama}</option>
        ))}
      </select>

      <select value={this.state.selectedDistrict} onChange={this.handleDistrictSelect}>
        <option value="">Select District</option>
        {kecamatan.filter(kec => kec.kabupaten === this.state.selectedRegency).map(kec => (
          <option key={kec.id} value={kec.nama}>{kec.nama}</option>
        ))}
      </select>

      <select value={this.state.selectedVillage} onChange={this.handleVillageSelect}>
        <option value="">Select Village</option>
        {desa.filter(des => des.kecamatan === this.state.selectedDistrict).map(des => (
          <option key={des.id} value={des.nama}>{des.nama}</option>
        ))}
      </select>
    
    </div>
  )
}

export default contohwilayah
