import React, {useState, useEffect} from 'react'
import axios from "axios";
import useSWR from 'swr';
import { useSWRConfig } from 'swr';
import Modal from '../../component/Modal';
// import {Link} from "react-router-dom";

const Pasien = () => {
    
    const [isOpen, setIsOpen] = useState(false);
    const [formData, setFormData] = useState({});
    const [editData, setEditData] = useState(null);

     const {mutate} = useSWRConfig();

      useEffect(() => {
    fetcher();
  }, []);

    const fetcher = async () => {
        const response = await axios.get('http://localhost:5000/rm');
        return response.data;
    };

    const {data} = useSWR('pasien', fetcher);
    if (!data) return <h2>loading....</h2>

    const deleteUser = async (id) => {
        await axios.delete(`http://localhost:5000/rm/${id}`); 
        mutate("pasien");
    };

    // handle submit data
    const handleSubmit = async (event) => {
        event.preventDefault();
  try {
    
    const response = await axios.post('http://localhost:5000/rm', formData);
    console.log(response.data);
    setIsOpen(false);
    mutate("pasien");
  } catch (error) {
    console.error(error);
  }
};

// handle change data
const handleFormChange = (event) => {
  setFormData({ ...formData, [event.target.name]: event.target.value });

  const { nama_lengkap, value } = event.target;
  setEditData(prevState => ({
    ...prevState,
    [nama_lengkap]: value
  }));
};

// edit data
const handleEdit = (data) => {
  setEditData(data);
  setIsOpen(true);
};


// mengirim request ke API untuk mengedit data
const handleEditSubmit = async (event) => {
  event.preventDefault();
  try {
    const response = await axios.put(`http://localhost:5000/rm/${editData.id}`, editData);
    console.log(response.data);
    setIsOpen(false);
    mutate(); // memperbarui data di cache
  } catch (error) {
    console.error(error);
  }
};


    // punya modal

    const handleCloseModal = () => {
        setIsOpen(false);
    };
    

  return (
    <div class="pasien">
        <div className='judul'>
            <h1 className='text-2xl text-left text-state-600 font-semibold'>Data Rekam Medis Pasien</h1>

            <button onClick={() => setIsOpen(true)} className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded'>New Pasien</button>
            
            {/* modal */}

            <Modal isOpen={isOpen} onClose={handleCloseModal}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                    Modal Title
                </h3>
                <div className="mt-2">
                     <form onSubmit={handleSubmit}>
                        <input type="text" name="nama_lengkap" onChange={handleFormChange} />
                        <button type="submit">Submit</button>
                    </form>
                </div>
                </div>
            </Modal>

            {/* modal */}


            {/* modal edit */}
           
           <Modal isOpen={isOpen} onClose={handleCloseModal}>
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                Edit Data
                </h3>
                <div className="mt-2">
                <form onSubmit={handleEditSubmit}>
                    <label>Nama Lengkap:</label>
                    <input type="text" name="nama_lengkap" value={editData.nama_lengkap} onChange={handleFormChange} />
                    <button type="submit">Update</button>
                </form>
                </div>
            </div>
            </Modal>

           
            {/* modal edit */}
           
            
            
            
            <div class="container mx-auto mt-4 py-4  bg-white">
                <table class="min-w-full divide-y divide-gray-200">
                    <thead>
                        <tr>
                        <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">No</th>
                        <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">No RM</th>
                        <th class="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Nama</th>
                        <th class="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Kitas</th>
                        <th class="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Kelamin</th>
                        <th class="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal Lahir</th>
                        <th class="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                    </thead>
                    <tbody class="bg-white divide-y divide-gray-200">
                        {data.map((pasien, index) => (
                    
                
                        <tr key={pasien.id}>
                 
                        <td class="px-0 py-1 whitespace-nowrap">
                            <div class="text-sm text-gray-900">{index + 1}</div>
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap">
                            <div class="text-sm text-gray-900"> {pasien.no_rm}</div>
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.nama_lengkap}
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.no_kitas}
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.kelamin}
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            {pasien.tanggal_lahir}
                        </td>
                        <td class="px-0 py-1 whitespace-nowrap text-sm text-gray-500 text-center">
                            Active
                        </td>
                        <td class="px-0 py-0 whitespace-nowrap text-center text-sm font-medium">
                            <button onClick={() => handleEdit(pasien)}  class="text-indigo-600 hover:text-indigo-900">Edit</button>
                            <button onClick={()=> deleteUser(pasien.id)} class="ml-4 text-red-600 hover:text-red-900">Delete</button>
                        </td>
                        </tr>
                  
                    ))}
                    
                    </tbody>

                 
                </table>

            </div>
        </div>
    </div>
  )
}

export default Pasien
